import {React, useState} from 'react'
import './Navbar.css'
import {Link} from 'react-router-dom'; 

export default function Navbar() {

    const [active, setActive] = useState(false);

    const home = document.getElementById("home");
    const work = document.getElementById("work");
    const contact = document.getElementById("contact");


   function homeActive(){
        home.classList.add(home.style.backgroundColor = 'white');
        home.classList.add(home.style.color = 'black');

   }

    return (

        <div className="navBar">

                <div className="containerLogo">
                    <Link to="/home">
                    <div className="navLogo" ><p>RELEASE</p></div>
                    </Link>
                </div>

                <div className="containerItems"> 
                    <Link to = "/contact">
                    <div id="contact" className="navItems"><p>Contact</p></div>
                    </Link>            

                    <Link to = "/work">
                    <div id ="work" className="navItems"><p>Work</p></div>
                    </Link>

                    <Link to = "/home">
                    <div id="home" className="navItems" onClick={homeActive}><p>Home</p></div>
                    </Link> 
                </div>

        </div>  

    )

}
