import { React, useState, useEffect } from 'react'
import './ImageTransition.css'

import image1 from "./images/print1.jpg";
import image2 from "./images/print2.jpg";
import image3 from "./images/print3.jpg";
import image4 from "./images/print4.jpg";

export default function ImageTransition() {

    //create counter state
    let [counter, setCounter] = useState(1);

    //create image state
    let [image, setImage] = useState(`url(${image1})`);

    let addCounter = () => {
        setCounter(counter + 1)
        //console.log('adiciona');
    }

    let handleImages = () => {

        if (counter === 1) {
            setImage(`url(${image1})`);
        } else if (counter === 2) {
            setImage(`url(${image2})`);
        } else if (counter === 3) {
            setImage(`url(${image3})`);
        } else if (counter === 4) {
            setImage(`url(${image4})`);
        } 

        document.getElementById("container").style.transition = "background-image 2s ease-in-out";
    }

    //change counter every second
    const interval = setTimeout(addCounter, 1000);
    clearTimeout(interval);

    useEffect(()=>{
        //handleImages();
        return ()=>counter=1;
    },[counter]);

    return (
        <div id="container" className="container" style={{ backgroundImage: image }} >
        </div>
    )
}
