import React from 'react'
import './ImageShow.css'
import image1 from "/image-show/print1.jpg";
import image2 from "/image-show/print2.jpg";

const imagesPath = {
    image1: 'image-show/print1.jpg',
    image2: "./public/image-show/print2.jpg"
}

class ImageShow extends React.Component {

    constructor(props) {
        super(props);
        this.state = { 
          bgImage: props.bgImage //so here I set the backroundImage with logo1, because I want to logo1 to be on the start of application, but when I change to logo2 I want to have that logo2 even when I refresh page ( but when I do it, constructor of App is setting it to the logo1. So maybe, can I save that logo even when the App is reloading to get the latest logo ?
        };
      }

      ChangeToLightMode = e => {
        this.setState({
          bgImage: `url(${image1})`
        });
      };

      ChangeToDarkMode = e => {
        this.setState({
          bgImage: `url(${image2})`
        });
      };

    render(){
        return(
            <div className="container" style={{ backgroundImage: this.state.bgImage }}>
                <div className="text-container"><h1 className="text">-------- Bernardo Branco</h1></div>
                <div><h2 className="heading">Digital Artist</h2></div>
                <div><h2 className="heading">X----------</h2></div>
                <div><h2 className="heading">IT Enthusiast</h2></div>
            </div>
        )
    }
}

export default ImageShow
