import React from 'react'
import './ImageTransition.css'

import image1 from "./images/print1.jpg";
import image2 from "./images/print2.jpg";
import image3 from "./images/print3.jpg";
import image4 from "./images/print4.jpg";

let counter = 1;
let imageDisplay = image1;

class ImageTransition extends React.Component {

    constructor(props) {
        super(props);
        this.state = { 
          bgImage: `url(${image1})`,
      }
        this.timeChanger = this.timeChanger.bind(this);
    }

    timeChanger(){

        counter += 1;

        if(counter===1){
        imageDisplay = `url(${image1})`
        document.getElementById("container").style.transition = "opacity 1s ease-in-out";
        } else if (counter===2){
        imageDisplay = `url(${image2})` 
        document.getElementById("container").style.transition = "opacity 1s ease-in-out";  
        } else if (counter===3){
        imageDisplay = `url(${image3})`
        document.getElementById("container").style.transition = "opacity 1s ease-in-out";   
        } else if (counter===4){
        imageDisplay = `url(${image4})`
        document.getElementById("container").style.transition = "opacity 1s ease-in-out";   
        }

        this.setState({bgImage: imageDisplay});

        console.log(imageDisplay,counter);

        if(counter>=5){
            counter = 1;
        }
    }

      componentDidMount() {
      setInterval(this.timeChanger, 5000);
      }


    render(){
        return(
            <div className="container" style={{ backgroundImage: this.state.bgImage}} >

                <div className="text-container">
                <h1 id ="nome" className="text">-------- Bernardo Branco</h1>
                <div><h2 className="heading">Digital Artist</h2></div>
                <div><h2 className="heading">X----------</h2></div>
                <div><h2 className="heading">IT Enthusiast</h2></div>
                </div>

            </div>
        )
    }
}

export default ImageTransition
