import React, {useState} from 'react'


let add=0;

export function useTime(){

    let [count, setCount] = useState(0);
    setTimeout(()=>{setCount(count)},1000);

    function handleTime(count){

        if (count >= 5){
            add=-1;
        } else if (count <= 0){
            add=1;
        }

        return add;
    }

    return count+handleTime(count);
}
