import {React, useState, useEffect} from 'react'
import './Navbar.css'
import {Link} from 'react-router-dom'; 

export default function Navbar() {

    let [counter, setCounter] = useState(0);

    useEffect(()=>{

            const home = document.getElementById("home");
            const work = document.getElementById("work");
            const contact = document.getElementById("contact");

            console.log(counter); 
            console.log(home.classList);
            console.log(work);
            console.log(contact);

            if(counter === 1){
                home.classList.remove("navItems");
                home.classList.add("navItems-active"); 
            } else  if(counter === 2){
                work.classList.remove("navItems");
                work.classList.add("navItems-active");      
            } else if(counter === 3){
                contact.classList.remove("navItems");
                contact.classList.add("navItems-active");     
            } 

    },[counter]);


    return (

        <div className="navBar">

                <div className="containerLogo">
                    <Link to="/home">
                    <div className="navLogo" ><p>RELEASE</p></div>
                    </Link>
                </div>

                <div className="containerItems"> 

                    <Link to = "/contact">
                    <div id="contact" className="navItems" onClick={()=>setCounter(3)}><p>Contact</p></div>
                    </Link>            

                    <Link to = "/work">
                    <div id ="work" className="navItems" onClick={()=>setCounter(2)}><p>Work</p></div>
                    </Link>

                    <Link to = "/home">
                    <div id="home" className="navItems" onClick={()=>setCounter(1)}><p>Home</p></div>
                    </Link> 
                </div>

        </div>   
    )




}
