import React from 'react'
import './Gallery.css'

import image1 from "./images/print1.jpg";
import image2 from "./images/print2.jpg";
import image3 from "./images/print3.jpg";
import image4 from "./images/print4.jpg";
import image5 from "./images/foto1.jpg";

let counter = 100
let crescent


class Gallery extends React.Component {

    timeChanger(){

      if (counter >= 100){
         crescent = false
      }

      if (counter <= 0){
        crescent = true
     }

      if (crescent){
        counter += 1
      } 

      if (!crescent){
        counter += 1
      } 


      document.getElementById("image").style.opacity = counter + "%"
      console.log(counter)
    }

    componentDidMount() {
    setInterval(this.timeChanger, 1000/30);
    }


    render(){
        return(

          <div>
            <ul>
              <li>
                <img src={image1} id="image"/>
              </li>
              <li>
                <img src={image2}/>
              </li>
              <li>
                <img src={image3}/>
              </li>
              <li>
                <img src={image5}/>
              </li>
              <li>
                <img src={image4}/>
              </li>
            </ul>
          </div>

        )
    }
}

export default Gallery
