import React from 'react'
import './Gallery.css'

import image1 from "./images/print1.jpg";
import image2 from "./images/print2.jpg";
import image3 from "./images/print3.jpg";
import image4 from "./images/print4.jpg";
import image5 from "./images/foto1.jpg";

let counter = 0
let crescent

class Gallery extends React.Component {

    timeChanger(){

      if (counter >= 100){
         crescent = false
      }

      if (counter <= 0){
        crescent = true
     }

      if (crescent){
        counter += 1
      } else {
        counter -= 1
      } 

      for (let i = 1; i<6; i++)
      document.getElementById("image" + i).style.opacity = counter + "%"
    }

    componentDidMount() {
    setInterval(this.timeChanger, 1000/30);
    }


    render(){

      const images = [
        {alt: 'image1', src: image1, id: 'image1' },
        {alt: 'image2', src: image2, id: 'image2' },
        {alt: 'image3', src: image3, id: 'image3' },
        {alt: 'image4', src: image4, id: 'image4' },
        {alt: 'image5', src: image5, id: 'image5' }
        ];

        console.logo(images.size)

        return(

          <div>
            <ul>
                {images.map(i => {
                    return <li><img alt={i.alt} src={i.src} id={i.id}/></li>
                })}
            </ul>
          </div>

        )
    }
}

export default Gallery
