import {React, useState, useEffect} from 'react'
import './Navbar.css'
import {Link} from 'react-router-dom'; 

export default function Navbar() {

    let [counter, setCounter] = useState(1);

    useEffect(()=>{

            const home = document.getElementById("home");
            const portfolio = document.getElementById("portfolio");
            const info = document.getElementById("info");
            const contact = document.getElementById("contact");

            if(counter === 1){
                home.classList.remove("navItems");
                home.classList.add("navItems-active"); 

                portfolio.classList.remove("navItems-active");
                portfolio.classList.add("navItems");
                info.classList.remove("navItems-active");
                info.classList.add("navItems");
                contact.classList.remove("navItems-active");
                contact.classList.add("navItems");

            } else  if(counter === 2){
                portfolio.classList.remove("navItems");
                portfolio.classList.add("navItems-active"); 

                home.classList.remove("navItems-active");
                home.classList.add("navItems");
                info.classList.remove("navItems-active");
                info.classList.add("navItems");
                contact.classList.remove("navItems-active");
                contact.classList.add("navItems");

            } else if(counter === 3){
                contact.classList.remove("navItems");
                contact.classList.add("navItems-active"); 

                home.classList.remove("navItems-active");
                home.classList.add("navItems");
                portfolio.classList.remove("navItems-active");
                portfolio.classList.add("navItems"); 
                info.classList.remove("navItems-active");
                info.classList.add("navItems");     
            } 

    },[counter]);


    return (

        <div className="navBar">

                <div className="containerLogo">
                    <Link to="/home">
                    <div className="navLogo" ><p>RELEASE</p></div>
                    </Link>
                </div>

                <div className="containerItems"> 

                    <Link to = "/contact">
                    <div id="contact" className="navItems" onClick={()=>setCounter(4)}><p>Contact</p></div>
                    </Link>  

                    <Link to = "/info">
                    <div id="info" className="navItems" onClick={()=>setCounter(3)}><p>Info</p></div>
                    </Link>            

                    <Link to = "/portfolio">
                    <div id ="portfolio" className="navItems" onClick={()=>setCounter(2)}><p>Work</p></div>
                    </Link>

                    <Link to = "/home">
                    <div id="home" className="navItems" onClick={()=>setCounter(1)}><p>Home</p></div>
                    </Link> 
                </div>

        </div>   
    )

}
