import { React, useState, useEffect } from 'react'
import './ImageTransition.css'

import image1 from "./images/print1.jpg";
import image2 from "./images/print2.jpg";
import image3 from "./images/print3.jpg";
import image4 from "./images/print4.jpg";

export default function ImageTransition() {

    //create counter state
    let [counter, setCounter] = useState(1);

    //create image state
    let [image, setImage] = useState(`url(${image1})`);

    //trigger counter + 1
    useEffect(()=>{
        let interval = setTimeout(addCounter, 3000); 
        return ()=>clearTimeout(interval); 
    });

    //increment counter
    let addCounter = () => {
        setCounter(counter+1)
        return counter;
    }

    let handleImages = ()=> {
    //handle Images according to counter
    if (counter === 1) {
        setImage(`url(${image1})`);
    } else if (counter === 2) {
        setImage(`url(${image2})`);
    } else if (counter === 3) {
        setImage(`url(${image3})`);
    } else if (counter === 4) {
        setImage(`url(${image4})`);
    } else if (counter >= 5){
        setCounter(1);
    }
}

    handleImages();

    //fade effect between images
    let estilo = document.getElementById("container").style.transition = "background-image 2s ease-in-out";

    return (
        <div id="container" className="container" style={{ backgroundImage: image }} >
        </div>
    )
}
