import React from 'react'
import './Gallery.css'

import image1 from "./images/print1.jpg";
import image2 from "./images/print2.jpg";
import image3 from "./images/print3.jpg";
import image4 from "./images/print4.jpg";
import image5 from "./images/foto1.jpg";

let counter = 100
let crescent


class Gallery extends React.Component {

    timeChanger(){

      if (counter >= 100){
         crescent = false
      }

      if (counter <= 0){
        crescent = true
     }

      if (crescent){
        counter += 1
      } 

      if (!crescent){
        counter -= 1
      } 

      document.getElementById("image").style.borderRadius = counter + "%"
    }

    componentDidMount() {
    setInterval(this.timeChanger, 1000/30);
    }


    render(){
        return(

          <div>
            <ul>
              <li>
                <img  alt = "image1" src={image1} id="image"/>
              </li>
              <li>
                <img alt = "image2" src={image2} id="image"/>
              </li>
              <li>
                <img alt = "image3" src={image3} id="image"/>
              </li>
              <li>
                <img alt = "image4" src={image5} id="image"/>
              </li>
              <li>
                <img alt = "image5"src={image4} id="image"/>
              </li>
            </ul>
          </div>

        )
    }
}

export default Gallery
