import './ImageShow.css'
import image1 from "public/image-show/print1.jpg";
import image2 from "../image-show/print2.jpg";
import image3 from "./image-show/print3.jpg";


var imageNumber = image1

function changeImageShow(){

  var number = image2

  if (number == image1) {
    number = image2
  } else if(number == image2){
    number = image1
  }

  return number

}

class ImageShow extends React.Component {

    constructor(props) {
        super(props);
        this.state = { 
          bgImage: `url(${image1})`
      }
    }

    render(){
        return(
            <div className="container" style={{ backgroundImage: this.state.bgImage }} >

                <div className="text-container"><h1 className="text">-------- Bernardo Branco</h1></div>
                <div><h2 className="heading">Digital Artist</h2></div>
                <div><h2 className="heading">X----------</h2></div>
                <div><h2 className="heading">IT Enthusiast</h2></div>

                <div className="button-container">
                <button className="button" onClick={() => this.setState({ bgImage: `url(${image1})` })}></button>
                <button className="button" onClick={() => this.setState({ bgImage: `url(${image2})` })}></button>

                </div>

            </div>
        )
    }
}

export default ImageShow
