import {React, useState, useEffect} from 'react'
import './Navbar.css'
import {Link} from 'react-router-dom'; 

export default function Navbar() {

    const [counter, setCounter] = useState(2);

    useEffect(()=>{
        function handleCounter(){

            const home = document.getElementById("home");
            const work = document.getElementById("work");
            const contact = document.getElementById("contact");

            if(counter === 1){
                home.classList.add(home.style.backgroundColor = 'white');
                home.classList.add(home.style.color = 'black');  
            } else {
                home.classList.remove(home.style.backgroundColor = 'white');
                home.classList.remove(home.style.color = 'black');  
            }

            if(counter === 2){
                work.classList.add(home.style.backgroundColor = 'white');
                work.classList.add(home.style.color = 'black');  
            } else {
                work.classList.remove(home.style.backgroundColor = 'white');
                work.classList.remove(home.style.color = 'black');  
            }

            if(counter === 3){
                contact.classList.add(home.style.backgroundColor = 'white');
                contact.classList.add(home.style.color = 'black');  
            } else {
                contact.classList.remove(home.style.backgroundColor = 'white');
                contact.classList.remove(home.style.color = 'black');  
            }
          } 
        handleCounter();
        console.log(counter);
    },[counter]);


    return (

        <div className="navBar">

                <div className="containerLogo">
                    <Link to="/home">
                    <div className="navLogo" ><p>RELEASE</p></div>
                    </Link>
                </div>

                <div className="containerItems"> 
                    <Link to = "/contact">
                    <div id="contact" className="navItems" onClick={()=>setCounter(3)}><p>Contact</p></div>
                    </Link>            

                    <Link to = "/work">
                    <div id ="work" className="navItems" onClick={()=>setCounter(2)}><p>Work</p></div>
                    </Link>

                    <Link to = "/home">
                    <div id="home" className="navItems" onClick={()=>setCounter(1)}><p>Home</p></div>
                    </Link> 
                </div>

        </div>   
    )




}
