import React from 'react'
import './ImageTransition.css'

import image1 from "./images/print1.jpg";
import image2 from "./images/print2.jpg";
import image3 from "./images/print3.jpg";
import image4 from "./images/print4.jpg";

let counter = 1;
let imageDisplay = image1;

 export default function ImageTransition() {

    let [image, setImage] = useState(`url(${image1})`);

        function changeImage(){

        if(counter===5){
            counter = 0;
        }

        counter += 1;

        if(counter===1){
        imageDisplay = `url(${image1})`
        document.getElementById("container").style.transition = "background-image 2s ease-in-out";
        } else if (counter===2){
        imageDisplay = `url(${image2})` 
        document.getElementById("container").style.transition = "background-image 2s ease-in-out"; 
        } else if (counter===3){
        imageDisplay = `url(${image3})`
        document.getElementById("container").style.transition = "background-image 2s ease-in-out";   
        } else if (counter===4){
        imageDisplay = `url(${image4})`
        document.getElementById("container").style.transition = "background-image 2s ease-in-out";   
        }

        setImage(imageDisplay);
      }

      setInterval(changeImage, 5000);


        return(
            <div id = "container" className="container" style={{ backgroundImage: this.state.bgImage}} >  
            </div>
        )

}
